CustomField.formula:
IF(ISPICKVAL(Priority, "High"),
    IMAGE("/img/samples/flag_red.gif", "Red Flag", 20, 20),
    IF(ISPICKVAL(Priority, "Medium"),
        IMAGE("/img/samples/flag_yellow.gif", "Yellow Flag", 20, 20),
        IF(ISPICKVAL(Priority, "Low"),
            IMAGE("/img/samples/flag_green.gif", "Green Flag", 20, 20),
            IMAGE("/img/samples/flag_default.gif", "Default Flag", 20, 20)
        )
    )
)
